
public class Calculadora {

	private int a;
	private int b;
	
	public Calculadora(int i, int j) {
		this.a=i;
		this.b=j;
	}

	public Object suma() {
		// TODO Auto-generated method stub
		return this.a+this.b;
	}

	public Object resta() {
		// TODO Auto-generated method stub
		return this.a-this.b;
	}

	public Object mult() {
		// TODO Auto-generated method stub
		return this.a*this.b;
	}

	public Object div() {
		// TODO Auto-generated method stub
		return this.a /this.b;
	}

}
